mapper.select:
SELECT rd.`workflow_execution_uuid` uuid, pu.`email` email , (SELECT pf.`file_name`FROM prs_file pf WHERE rd.`file_id`=pf.`id`) fileNameGWAS, (SELECT pf.`file_name`FROM prs_file pf WHERE rd.`LD_file_id`=pf.`id`) fileNameLD, al.`NAME` algorithmsName , rd.`status` runnerStatus , rd.`job_name` jobName , rd.`result_path` resultPath, rd.`progress` runnerProgress, rd.`queue` runnerQueue, DATE_FORMAT(rd.`created_date` , '%Y-%m-%d') createdDate, pe.`parameter_name` as children_parameterName, pe.`parameter_value` as children_parameterValue FROM runner_detail rd LEFT JOIN PRS_USER pu ON rd.`user_id`=pu.`id` LEFT JOIN parameter_enter pe ON rd.`workflow_execution_uuid`=pe.`workflow_execution_uuid` LEFT JOIN algorithms al ON pe.`algorithms_id`=al.`id` WHERE rd.`is_delete`=0
AND rd.`user_id`=#{userId}
AND rd.`workflow_execution_uuid`=#{workflowExecutionUuid}
ORDER BY rd.`created_date` DESC
